import React from 'react'

class PeopleList extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
       list :["hole","karim","nabil"]
    }
  }


// function handleClick(index){
//   props.removeFunc(index)
// }

handleClick = (index) => {
  const newArray = this.state.list.splice(index,1);
  this.setState({
    newArray
  })
}

render(){

  return (

    <section>
    <p className='text-noti'>You have {this.state.list.length} notifications</p>
     {
      this.state.list.map((user,index) => (
        <div key={index} className="person">

          <div className="avatar">
                <img src={user}/>
            </div>
            <div className="info">
              <h1>{user}</h1>
              <p>{user} years</p>
            </div>

            <button className='closeBtn' onClick={handleClick(index)} >x</button>

        </div>
      ))
     }

    </section>
  )
}
}




export default PeopleList
