import React from 'react'

function PeopleList(props) {
  const array = props.person


  function removeUser(index){

    const arr = props.person
    arr.splice(index,1);
    console.log(index);
  }



  return (

    <section>
    <p className='text-noti'>You have {array.length} notifications</p>
     {
      array.map((user,index)=>(
        <div key={index} className="person">
        <div className="avatar">
              <img src={user.img}/>
          </div>
          <div className="info">
            <h1>{user.name}</h1>
            <p>{user.age} years</p>
          </div>

            <button className='closeBtn' onClick={()=>removeUser(index)}>x</button>

        </div>
      ))
     }

    </section>
  )
}

export default PeopleList
