import React, { useState } from 'react'

function PeopleList(props) {

  const [list,setList] = useState(["hassan","karim","fathi"])


// function handleClick(index){
//   props.removeFunc(index)
// }

function handleClick(index){
  setList(list.splice(index,1))
}



  return (

    <section>
    <p className='text-noti'>You have {list.length} notifications</p>
     {
      list.map((user,index)=>(
        <div key={index} className="person">
        <div className="avatar">
              <img src={user.img}/>
          </div>
          <div className="info">
            <h1>{user}</h1>
            <p>{user.age} years</p>
          </div>

            <button className='closeBtn' onClick={()=>handleClick(index)} >x</button>

        </div>
      ))
     }

    </section>
  )
}

export default PeopleList
